	    var processRuleEffect = function processRuleEffect(event, callerId) {
	
	        if (event === 'registration') {
	            //If the current event is registration, handle this in own method
	            processRegistrationRuleEffect(event, callerId);
	            return;
	        }
	        //Establish which event was affected:
	        var affectedEvent = $scope.currentEvent;
	        if (!affectedEvent || !affectedEvent.event) {
	            //The data entry widget does not have an event selected.
	            return;
	        } else if (event === 'dataEntryInit') {
	            //The data entry widget is associated with an event, 
	            //and therefore we do not want to process rule effects from the registration form.
	            return;
	        }
	
	        if (event !== affectedEvent.event) {
	            //if the current event is not the same as the affected event, 
	            //the effecs should be disregarded in the current events controller instance.
	            return;
	        }
	
	        $scope.assignedFields[event] = [];
	        $scope.hiddenSections[event] = [];
	        $scope.warningMessages[event] = [];
	        $scope.errorMessages[event] = [];
	        $scope.hiddenFields[event] = [];
	        $scope.mandatoryFields[event] = [];
	        $scope.optionVisibility[event] = { showOnly: null, hidden: {} };;
	
	        var dataElementOptionsChanged = [];
	
	        angular.forEach($rootScope.ruleeffects[event], function (effect) {
	            //in the data entry controller we only care about the "hidefield", showerror and showwarning actions
	            if (effect.action === "HIDEFIELD") {
	                if (effect.dataElement) {
	
	                    if (affectedEvent.status !== 'SCHEDULE' && affectedEvent.status !== 'SKIPPED' && !affectedEvent.editingNotAllowed) {
	                        if (effect.ineffect && affectedEvent[effect.dataElement.id]) {
	                            //If a field is going to be hidden, but contains a value, we need to take action;
	                            if (effect.content) {
	                                //TODO: Alerts is going to be replaced with a proper display mecanism.
	                                alert(effect.content);
	                            } else {
	                                //TODO: Alerts is going to be replaced with a proper display mecanism.
	                                alert($scope.prStDes[effect.dataElement.id].dataElement.displayFormName + " was blanked out and hidden by your last action");
	                            }
	
	                            //Blank out the value:
	                            affectedEvent[effect.dataElement.id] = "";
	                            $scope.saveDataValueForEvent($scope.prStDes[effect.dataElement.id], null, affectedEvent, true);
	                        }
	                    }
	
	                    if (effect.ineffect) {
	                        $scope.hiddenFields[event][effect.dataElement.id] = true;
	                    } else if (!$scope.hiddenFields[event][effect.dataElement.id]) {
	                        $scope.hiddenFields[event][effect.dataElement.id] = false;
	                    }
	                } else {
	                    if (!effect.trackedEntityAttribute) {
	                        $log.warn("ProgramRuleAction " + effect.id + " is of type HIDEFIELD, bot does not have a field defined");
	                    }
	                }
	            } else if (effect.action === "SHOWERROR" || effect.action === "ERRORONCOMPLETE") {
	                if (effect.ineffect) {
	                    var message = effect.content + (effect.data ? effect.data : "");
	
	                    if (effect.dataElement && $scope.prStDes[effect.dataElement.id]) {
	                        if (effect.action === "SHOWERROR") {
	                            //only SHOWERROR messages is going to be shown in the form as the user works
	                            $scope.errorMessages[event][effect.dataElement.id] = message;
	                        }
	                        $scope.errorMessages[event].push($translate.instant($scope.prStDes[effect.dataElement.id].dataElement.displayName) + ": " + message);
	                    } else {
	                        $scope.errorMessages[event].push(message);
	                    }
	                }
	            } else if (effect.action === "SHOWWARNING" || effect.action === "WARNINGONCOMPLETE") {
	                if (effect.ineffect) {
	                    var message = effect.content + (effect.data ? effect.data : "");
	
	                    if (effect.dataElement && $scope.prStDes[effect.dataElement.id]) {
	                        if (effect.action === "SHOWWARNING") {
	                            //only SHOWWARNING messages is going to show up in the form as the user works
	                            $scope.warningMessages[event][effect.dataElement.id] = message;
	                        }
	                        $scope.warningMessages[event].push($translate.instant($scope.prStDes[effect.dataElement.id].dataElement.displayName) + ": " + message);
	                    } else {
	                        $scope.warningMessages[event].push(message);
	                    }
	                }
	            } else if (effect.action === "HIDESECTION") {
	                if (effect.programStageSection) {
	                    if (effect.ineffect) {
	                        $scope.hiddenSections[event][effect.programStageSection] = true;
	                    } else if (!$scope.hiddenSections[event][effect.programStageSection]) {
	                        $scope.hiddenSections[event][effect.programStageSection] = false;
	                    }
	                } else {
	                    $log.warn("ProgramRuleAction " + effect.id + " is of type HIDESECTION, bot does not have a section defined");
	                }
	            } else if (effect.action === "ASSIGN") {
	                if (affectedEvent.status !== 'SCHEDULE' && affectedEvent.status !== 'SKIPPED' && !affectedEvent.editingNotAllowed) {
	                    if (effect.ineffect && effect.dataElement && $scope.prStDesInStage[$scope.currentStage.id][effect.dataElement.id]) {
	                        //For "ASSIGN" actions where we have a dataelement, we save the calculated value to the dataelement:
	                        //Blank out the value:
	                        var processedValue = $filter('trimquotes')(effect.data);
	
	                        if ($scope.prStDes[effect.dataElement.id].dataElement.optionSet) {
	                            processedValue = OptionSetService.getName($scope.optionSets[$scope.prStDes[effect.dataElement.id].dataElement.optionSet.id].options, processedValue);
	                        }
	                        var prStDe = $scope.prStDes[effect.dataElement.id];
	                        processedValue = CommonUtils.formatDataValue(affectedEvent.event, processedValue, prStDe.dataElement, $scope.optionSets, 'USER');
	
	                        affectedEvent[effect.dataElement.id] = processedValue;
	                        $scope.assignedFields[event][effect.dataElement.id] = true;
	
	                        if (callerId === $scope.instanceId) {
	                            $scope.saveDataValueForEvent(prStDe, null, affectedEvent, true);
	                        }
	                    }
	                }
	            } else if (effect.action === "SETMANDATORYFIELD") {
	                if (effect.dataElement) {
	                    $scope.mandatoryFields[event][effect.dataElement.id] = effect.ineffect;
	                }
	            } else if (effect.action === "HIDEPROGRAMSTAGE") {
	                if (effect.programStage) {
	                    if ($scope.stagesNotShowingInStageTasks[effect.programStage.id] !== effect.ineffect) {
	                        $scope.stagesNotShowingInStageTasks[effect.programStage.id] = effect.ineffect;
	                    }
	                } else {
	                    $log.warn("ProgramRuleAction " + effect.id + " is of type HIDEPROGRAMSTAGE, bot does not have a stage defined");
	                }
	            } else if (effect.action === "HIDEOPTION") {
	                if (effect.ineffect && effect.dataElement && effect.option) {
	                    if (!$scope.optionVisibility[event][effect.dataElement.id]) $scope.optionVisibility[event][effect.dataElement.id] = { hidden: {} };
	                    $scope.optionVisibility[event][effect.dataElement.id].hidden[effect.option.id] = { id: effect.option.id };
	                    if (dataElementOptionsChanged.indexOf(effect.dataElement.id) === -1) dataElementOptionsChanged.push(effect.dataElement.id);
	                }
	            } else if (effect.action === "SHOWOPTIONGROUP") {
	                if (effect.ineffect && effect.dataElement && effect.optionGroup) {
	                    if (!$scope.optionVisibility[event][effect.dataElement.id]) $scope.optionVisibility[event][effect.dataElement.id] = { hidden: {} };
	                    var optionGroup = $scope.optionGroupsById[effect.optionGroup.id];
	                    if (optionGroup) {
	                        if (!$scope.optionVisibility[event][effect.dataElement.id].showOnly) $scope.optionVisibility[event][effect.dataElement.id].showOnly = {};
	                        angular.extend($scope.optionVisibility[event][effect.dataElement.id].showOnly, optionGroup.optionsById);
	                        if (dataElementOptionsChanged.indexOf(effect.dataElement.id) === -1) dataElementOptionsChanged.push(effect.dataElement.id);
	                    } else {
	                        $log.warn("OptionGroup " + effect.optionGroup.id + " was not found");
	                    }
	                }
	            } else if (effect.action === "HIDEOPTIONGROUP") {
	                if (effect.ineffect && effect.dataElement && effect.optionGroup) {
	                    if (!$scope.optionVisibility[event][effect.dataElement.id]) $scope.optionVisibility[event][effect.dataElement.id] = { hidden: {} };
	                    var optionGroup = $scope.optionGroupsById[effect.optionGroup.id];
	                    if (optionGroup) {
	                        angular.extend($scope.optionVisibility[event][effect.dataElement.id].hidden, optionGroup.optionsById);
	                        if (dataElementOptionsChanged.indexOf(effect.dataElement.id) === -1) dataElementOptionsChanged.push(effect.dataElement.id);
	                    } else {
	                        $log.warn("OptionGroup " + effect.optionGroup.id + " was not found");
	                    }
	                }
	            }
	        });
	
	        //Run after effects are applied. Makes it possible to use multiple showOptionGroup effects.
	        clearValueForShowHideOptionActions(dataElementOptionsChanged, affectedEvent);
	
	        updateTabularEntryStages();
	        $rootScope.$broadcast('tei-report-widget', { events: $scope.allEventsSorted });
	    };
	
	    var clearValueForShowHideOptionActions = function clearValueForShowHideOptionActions(dataElements, affectedEvent) {
	        //Dont process if editing not allowed
	        if (['SCHEDULE', 'SKIPPED'].indexOf(affectedEvent.status) === -1 && !affectedEvent.editingNotAllowed) {
	            dataElements.forEach(function (de) {
	                var value = affectedEvent[de];
	                //Only process if has selected value
	                if (angular.isDefined(value) && value !== "") {
	                    var optionSet = $scope.optionSets[$scope.prStDes[de].dataElement.optionSet.id];
	                    //Find selectedOption by displayName
	                    var selectedOption = optionSet.options.find(function (o) {
	                        return o.displayName === value;
	                    });
	                    var shouldClear = !selectedOption;
	
	                    //If has selected option and a option is not in showOnly or is in hidden, field should be cleared.
	                    if (selectedOption) {
	                        shouldClear = $scope.optionVisibility[affectedEvent.event][de].showOnly && !$scope.optionVisibility[affectedEvent.event][de].showOnly[selectedOption.id] || $scope.optionVisibility[affectedEvent.event][de].hidden[selectedOption.id];
	                    }
	
	                    if (shouldClear) {
	                        var message = $scope.prStDes[de].dataElement.displayFormName + ' was blanked out because the option "' + value + '" got hidden by your last action';
	                        alert(message);
	                        affectedEvent[de] = "";
	                        $scope.saveDataValueForEvent($scope.prStDes[de], null, affectedEvent, true);
	                    }
	                }
	            });
	        }
	    };
	
