	                    newEvent.dueDateLabel = dummyEvent.dueDateLabel;
	                    newEvent.sortingDate = ev.eventDate ? ev.eventDate : ev.dueDate, newEvent.statusColor = EventUtils.getEventStatusColor(ev);
